import React, { useState } from 'react';
import { Link, useNavigate } from 'react-router-dom';
import './PaymentPage.css';

const PaymentPage = () => {
  const [paymentMethod, setPaymentMethod] = useState('upi');
  const [uploadedImage, setUploadedImage] = useState<File | null>(null);
  const [previewSrc, setPreviewSrc] = useState('');
  const [formData, setFormData] = useState({
    name: '',
    email: '',
    phone: '',
    transactionId: ''
  });
  
  const navigate = useNavigate();

  // Handle form field changes
  const handleInputChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value
    });
  };

  // Switch between payment methods
  const handlePaymentMethodChange = (method: string) => {
    setPaymentMethod(method);
  };

  // Handle file upload
  const handleFileChange = (e: React.ChangeEvent<HTMLInputElement>) => {
    const file = e.target.files?.[0];
    if (!file) return;

    setUploadedImage(file);

    const reader = new FileReader();
    reader.onload = () => {
      setPreviewSrc(reader.result as string);
    };
    reader.readAsDataURL(file);
  };

  // Handle form submission
  const handleSubmit = (e: React.FormEvent) => {
    e.preventDefault();
    
    // Basic validation
    if (!formData.name || !formData.email || !formData.transactionId || !uploadedImage) {
      alert('Please fill in all required fields and upload a payment screenshot.');
      return;
    }
    
    // Here you would typically send the data to your server
    console.log('Submitting payment information:', {
      ...formData,
      paymentMethod,
      uploadedImage
    });
    
    // Display success message
    alert('Payment submitted successfully! We will verify your payment and update your registration status.');
    
    // Redirect to confirmation page or dashboard
    navigate('/registration-success');
  };

  // Handle cancel action
  const handleCancel = () => {
    if (window.confirm('Are you sure you want to cancel this payment? Your registration will remain incomplete.')) {
      navigate('/register');
    }
  };

  return (
    <div className="payment-page">
      <div className="payment-header">
        <h1>Complete Your Payment</h1>
        <p>Please make your payment to confirm your registration for Infinity 2025 events.</p>
      </div>

      <form onSubmit={handleSubmit}>
        <div className="payment-container">
          <div className="payment-form-section">
            <h2>Payment Details</h2>
            
            <div className="form-group">
              <label htmlFor="name">Full Name</label>
              <input 
                type="text" 
                id="name" 
                name="name" 
                className="form-control" 
                value={formData.name}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="email">Email Address</label>
              <input 
                type="email" 
                id="email" 
                name="email" 
                className="form-control" 
                value={formData.email}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="form-group">
              <label htmlFor="phone">Phone Number</label>
              <input 
                type="tel" 
                id="phone" 
                name="phone" 
                className="form-control" 
                value={formData.phone}
                onChange={handleInputChange}
                required
              />
            </div>
            
            <div className="payment-method">
              <div className="payment-method-title">Choose Payment Method</div>
              <div className="payment-method-options">
                <div 
                  className={`payment-method-option ${paymentMethod === 'upi' ? 'selected' : ''}`}
                  onClick={() => handlePaymentMethodChange('upi')}
                >
                  <img 
                    src="/images/upi-icon.png" 
                    alt="UPI" 
                    onError={(e) => (e.target as HTMLImageElement).src = '/images/placeholder.jpg'} 
                  />
                  <span>UPI</span>
                </div>
                <div 
                  className={`payment-method-option ${paymentMethod === 'netbanking' ? 'selected' : ''}`}
                  onClick={() => handlePaymentMethodChange('netbanking')}
                >
                  <i className="fas fa-university"></i>
                  <span>Net Banking</span>
                </div>
                <div 
                  className={`payment-method-option ${paymentMethod === 'card' ? 'selected' : ''}`}
                  onClick={() => handlePaymentMethodChange('card')}
                >
                  <i className="far fa-credit-card"></i>
                  <span>Card</span>
                </div>
              </div>
            </div>

            {paymentMethod === 'upi' && (
              <div className="upi-details">
                <p>Please make payment to the following UPI ID:</p>
                <div className="upi-id">infinity2025@okaxis</div>
                
                <div className="qr-code">
                  <img 
                    src="/images/payment-qr.png" 
                    alt="UPI QR Code" 
                    onError={(e) => (e.target as HTMLImageElement).src = '/images/placeholder.jpg'} 
                  />
                </div>
                
                <div className="form-group">
                  <label htmlFor="transactionId">UPI Transaction ID / Reference Number</label>
                  <input 
                    type="text" 
                    id="transactionId" 
                    name="transactionId" 
                    className="form-control" 
                    value={formData.transactionId}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                
                <div className="upload-section">
                  <p>After making payment, upload screenshot for verification:</p>
                  
                  <label className="file-upload">
                    <input type="file" accept="image/*" onChange={handleFileChange} required />
                    <i className="fas fa-cloud-upload-alt"></i>
                    <div className="file-upload-text">
                      <p>Drag & Drop or Click to Upload</p>
                      <span>Supported formats: JPG, PNG</span>
                    </div>
                  </label>
                  
                  {previewSrc && (
                    <div className="preview-image">
                      <img src={previewSrc} alt="Payment Screenshot" />
                    </div>
                  )}
                </div>
              </div>
            )}
            
            {paymentMethod === 'netbanking' && (
              <div className="upi-details">
                <p>Please transfer funds to the following account:</p>
                <div className="bank-details">
                  <div className="form-group">
                    <label>Account Number</label>
                    <div className="upi-id">1234567890</div>
                  </div>
                  <div className="form-group">
                    <label>IFSC Code</label>
                    <div className="upi-id">SBIN0001234</div>
                  </div>
                  <div className="form-group">
                    <label>Account Holder</label>
                    <div className="upi-id">Infinity 2025</div>
                  </div>
                  <div className="form-group">
                    <label>Bank</label>
                    <div className="upi-id">State Bank of India</div>
                  </div>
                </div>
                
                <div className="form-group">
                  <label htmlFor="transactionId">Transaction ID / Reference Number</label>
                  <input 
                    type="text" 
                    id="transactionId" 
                    name="transactionId" 
                    className="form-control" 
                    value={formData.transactionId}
                    onChange={handleInputChange}
                    required
                  />
                </div>
                
                <div className="upload-section">
                  <p>After making payment, upload screenshot for verification:</p>
                  
                  <label className="file-upload">
                    <input type="file" accept="image/*" onChange={handleFileChange} required />
                    <i className="fas fa-cloud-upload-alt"></i>
                    <div className="file-upload-text">
                      <p>Drag & Drop or Click to Upload</p>
                      <span>Supported formats: JPG, PNG</span>
                    </div>
                  </label>
                  
                  {previewSrc && (
                    <div className="preview-image">
                      <img src={previewSrc} alt="Payment Screenshot" />
                    </div>
                  )}
                </div>
              </div>
            )}
            
            {paymentMethod === 'card' && (
              <div className="upi-details">
                <p>We are currently not accepting direct card payments.</p>
                <p>Please use UPI or Net Banking options instead.</p>
              </div>
            )}
          </div>
          
          <div className="payment-details-section">
            <h2>Registration Summary</h2>
            
            <div className="registration-summary">
              <div className="summary-item">
                <div className="summary-label">Registration ID</div>
                <div className="summary-value">INF25-7842</div>
              </div>
              <div className="summary-item">
                <div className="summary-label">Event Package</div>
                <div className="summary-value">Technical + Cultural</div>
              </div>
              <div className="summary-item">
                <div className="summary-label">Selected Events</div>
                <div className="summary-value">3 Events</div>
              </div>
              <div className="summary-item">
                <div className="summary-label">Registration Fee</div>
                <div className="summary-value">₹800</div>
              </div>
              <div className="summary-item">
                <div className="summary-label">Workshop Fee</div>
                <div className="summary-value">₹400</div>
              </div>
              <div className="summary-item">
                <div className="summary-label">Accommodation</div>
                <div className="summary-value">₹300 (1 Night)</div>
              </div>
            </div>
            
            <div className="total-amount">
              <div className="summary-label">Total Amount</div>
              <div className="summary-value">₹1,500</div>
            </div>
            
            <div className="payment-note">
              <p>
                <i className="fas fa-info-circle"></i> 
                Your registration will be confirmed once the payment is verified. This usually takes 24-48 hours.
              </p>
            </div>
            
            <div className="action-buttons">
              <button type="submit" className="btn-payment btn-primary">
                Submit Payment
              </button>
              <button type="button" onClick={handleCancel} className="btn-payment btn-secondary">
                Cancel
              </button>
            </div>
          </div>
        </div>
      </form>
    </div>
  );
};

export default PaymentPage;
